// src/components/Participation.js
import React from "react";
import Section from "./Section";

const Participation = () => {
  return (
    <Section>
      <div className="max-w-xl mx-auto text-center text-gray-800 mt-8 space-y-6">
        <h2 className="text-2xl sm:text-3xl font-playfair font-bold">
          Contribution / Participation
        </h2>
        <p className="text-lg">
          Si vous souhaitez participer à notre cadeau de mariage ou contribuer, vous pouvez effectuer un virement sur le compte suivant :
        </p>
        <p className="text-xl font-bold mt-2 bg-gray-100 py-2 px-4 rounded-lg select-all">
          IBAN : CH12 3456 7890 1234 5678 9
        </p>
        <p className="text-gray-600 italic text-sm">
          Merci pour votre générosité et votre soutien ! ❤️
        </p>
      </div>
    </Section>
  );
};

export default Participation;
