import { useState, useEffect, useRef } from "react";
import Countdown from "./components/Countdown";
import Section from "./components/Section";
import { ChevronDown, ChevronUp } from "lucide-react";

function App() {
  const [activeTab, setActiveTab] = useState("programme");
  const [scrolled, setScrolled] = useState(false); // Indicateur de défilement
  const [scrollDirection, setScrollDirection] = useState(null); // Suivi de la direction du scroll
  const heroRef = useRef(null); // Référence à la page d'accueil
  const mainRef = useRef(null); // Référence à la section des onglets

  const tabs = [
    { id: "programme", label: "Programme" },
    { id: "acces", label: "Accès" },
    { id: "rsvp", label: "RSVP" },
  ];

  const handleScroll = (e) => {
    // Si on scrolle vers le bas et qu'on est encore sur la page d'accueil
    if (e.deltaY > 10 && !scrolled) {
      setScrolled(true);
      setScrollDirection("down");
      // Ajouter une animation de disparition pour la page d'accueil
      heroRef.current.style.transition = "opacity 1s ease-out";
      heroRef.current.style.opacity = 0; // Faire disparaître la page d'accueil
      mainRef.current.scrollIntoView({ behavior: "smooth" });
    }
    // Si on scrolle vers le haut et qu'on est dans les onglets
    else if (e.deltaY < -10 && scrolled) {
      setScrolled(false);
      setScrollDirection("up");
      // Ajouter une animation de réapparition pour la page d'accueil
      heroRef.current.style.transition = "opacity 1s ease-in";
      heroRef.current.style.opacity = 1; // Réapparaître la page d'accueil
      heroRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  useEffect(() => {
    const hero = heroRef.current;
    if (hero) {
      hero.addEventListener("wheel", handleScroll, { passive: true });
    }

    return () => {
      if (hero) {
        hero.removeEventListener("wheel", handleScroll);
      }
    };
  }, [scrolled]);

  return (
    <div className="font-sans text-gray-800">
      {/* Page d'accueil */}
      <section
        ref={heroRef}
        className="relative h-screen text-white flex items-center justify-center"
        style={{
          backgroundImage: "url('/background.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div className="absolute inset-0 bg-black/40 backdrop-blur-sm"></div>
        <div className="relative z-10 text-center px-4">
          <h1 className="text-5xl md:text-6xl font-bold">Lise & Thibault</h1>
          <p className="text-xl mt-4 mb-6">Notre mariage – 2026</p>
          <Countdown targetDate="2026-07-25T00:00:00" />
          <div className="mt-12 animate-bounce text-white/80">
            <ChevronDown size={32} />
          </div>
        </div>
      </section>

      {/* Contenu principal (Page avec les onglets) */}
      <div ref={mainRef} id="main">
        {/* Navigation par onglets */}
        <nav className="sticky top-0 z-10 bg-white shadow flex justify-center space-x-6 py-4">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`px-4 py-2 rounded-full transition-all duration-200 text-sm md:text-base ${
                activeTab === tab.id
                  ? "bg-gray-800 text-white shadow"
                  : "bg-gray-100 text-gray-600 hover:bg-gray-200"
              }`}
            >
              {tab.label}
            </button>
          ))}
        </nav>

        {/* Contenu selon l’onglet actif */}
        <main className="px-4 py-10">
          {activeTab === "programme" && (
            <Section title="Programme">
              <p>14h00 – Cérémonie</p>
              <p>16h00 – Cocktail</p>
              <p>19h00 – Dîner</p>
              <p>22h00 – Soirée</p>
            </Section>
          )}
          {activeTab === "acces" && (
            <Section title="Accès">
              <p>[Adresse du lieu]</p>
              <p>[Infos parking, navette, etc.]</p>
            </Section>
          )}
          {activeTab === "rsvp" && (
            <Section title="RSVP">
              <p>Un formulaire RSVP sera bientôt disponible ici.</p>
            </Section>
          )}
        </main>

        {/* Section vide pour prolonger la page et assurer une hauteur suffisante */}
        <div
          className="bg-gray-50"
          style={{ height: `100px` }}
        ></div>

        {/* Pied de page */}
        <footer className="text-center text-sm text-gray-500 py-6">
          © 2026 Lise & Thibault
        </footer>
      </div>
    </div>
  );
}

export default App;
