// Exemple d'App.js

import { useState } from "react";
import Section from "./Section"; // Si tu as la fonction Section dans un fichier séparé
import Tab from "./Tab"; // Supposons que tu utilises un composant Tab pour les onglets

function App() {
  const [activeTab, setActiveTab] = useState("programme");

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="App">
      <div className="tabs">
        <div className="flex justify-center space-x-4">
          <Tab onClick={() => handleTabClick("programme")} active={activeTab === "programme"}>Programme</Tab>
          <Tab onClick={() => handleTabClick("seloger")} active={activeTab === "seloger"}>Se Loger</Tab>
          <Tab onClick={() => handleTabClick("acces")} active={activeTab === "acces"}>Accès</Tab>
          <Tab onClick={() => handleTabClick("rsvp")} active={activeTab === "rsvp"}>RSVP</Tab>
        </div>
      </div>

      <div className="tab-content">
        {activeTab === "programme" && (
          <Section title="Programme">
            <div className="mx-auto text-center">
              {/* Contenu de la tab Programme */}
              <p>Contenu détaillé du programme ici.</p>
            </div>
          </Section>
        )}
        {activeTab === "seloger" && (
          <Section title="Se Loger">
            <div className="mx-auto text-center">
              {/* Contenu de la tab Se Loger */}
              <p>Informations sur les options de logement.</p>
            </div>
          </Section>
        )}
        {activeTab === "acces" && (
          <Section title="Accès">
            <div className="mx-auto text-center">
              {/* Contenu de la tab Accès */}
              <p>Instructions pour accéder à l'événement.</p>
            </div>
          </Section>
        )}
        {activeTab === "rsvp" && (
          <Section title="RSVP">
            <div className="mx-auto text-center">
              {/* Contenu de la tab RSVP */}
              <p>Formulaire RSVP et informations de confirmation.</p>
            </div>
          </Section>
        )}
      </div>
    </div>
  );
}

export default App;
