// src/components/Participation.js
import React from "react";
import Section from "./Section";

const Participation = () => {
  return (
    <Section>
      <div className="max-w-2xl sm:max-w-4xl mx-auto text-gray-800 mt-6 text-center space-y-6">
        {/* --- Participation --- */}
        <p className="text-base sm:text-lg text-gray-700 italic mb-6">
          Si vous souhaitez nous faire un cadeau, participer à notre voyage de noces ou contribuer à notre mariage, vous pouvez effectuer un virement sur l’un des comptes suivants :
        </p>

        <ul className="space-y-6">
          <li>
            <p className="text-lg sm:text-xl font-bold bg-gray-100 py-2 px-4 rounded-lg select-all">
              IBAN 1 : CH84 0022 8228 1604 7240 Q
            </p>
          </li>
          <li>
            <p className="text-lg sm:text-xl font-bold bg-gray-100 py-2 px-4 rounded-lg select-all">
              IBAN 2 : FR76 2823 3000 0122 8094 9212 528
            </p>
          </li>
        </ul>

        <p className="text-base sm:text-lg text-gray-700 italic mt-6">
          Merci pour votre générosité et votre soutien ! ❤️
        </p>
      </div>
    </Section>
  );
};

export default Participation;
