import { useState } from "react";
import Countdown from "./components/Countdown";
import Section from "./components/Section";

function App() {
  const [activeTab, setActiveTab] = useState("programme");

  const tabs = [
    { id: "programme", label: "Programme" },
    { id: "acces", label: "Accès" },
    { id: "rsvp", label: "RSVP" },
  ];

  return (
    <div className="font-sans text-gray-800">
      <header className="bg-white py-12 text-center shadow">
        <h1 className="text-4xl font-bold">Lise & Thibault</h1>
        <p className="text-lg mt-2">Notre mariage - 2026</p>
        <Countdown targetDate="2026-07-25T00:00:00" />
      </header>

      {/* Onglets */}
      <nav className="flex justify-center space-x-6 mt-6">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`px-4 py-2 rounded-full transition-all duration-200 text-sm md:text-base ${
              activeTab === tab.id
                ? "bg-gray-800 text-white shadow"
                : "bg-gray-100 text-gray-600 hover:bg-gray-200"
            }`}
          >
            {tab.label}
          </button>
        ))}
      </nav>

      {/* Contenu selon l’onglet sélectionné */}
      <main className="px-4 py-8">
        {activeTab === "programme" && (
          <Section title="Programme">
            <p>14h00 - Cérémonie</p>
            <p>16h00 - Cocktail</p>
            <p>19h00 - Dîner</p>
            <p>22h00 - Soirée</p>
          </Section>
        )}
        {activeTab === "acces" && (
          <Section title="Accès">
            <p>[Adresse du lieu]</p>
            <p>[Infos parking, navette, etc.]</p>
          </Section>
        )}
        {activeTab === "rsvp" && (
          <Section title="RSVP">
            <p>Un formulaire RSVP sera bientôt disponible ici.</p>
          </Section>
        )}
      </main>

      <footer className="text-center text-sm text-gray-500 py-4">
        © 2026 Lise & Thibault
      </footer>
    </div>
  );
}

export default App;
