import { useState, useRef, useEffect } from "react";
import Countdown from "./components/Countdown";
import { ChevronDown } from "lucide-react";

// Importer les composants des sections
import SeLoger from "./components/SeLoger";

function App() {
  const [activeTab, setActiveTab] = useState("programme");
  const mainRef = useRef(null);
  const heroRef = useRef(null);

  const tabs = [
    { id: "programme", label: "Programme" },
    { id: "seloger", label: "Se Loger" },
    { id: "acces", label: "Accès" },
    { id: "rsvp", label: "RSVP" },
  ];

  // Fonction de scroll fluide lorsque l'utilisateur fait défiler l'écran
  useEffect(() => {
    const handleWheel = (e) => {
      if (e.deltaY > 10 && mainRef.current) {
        mainRef.current.scrollIntoView({ behavior: "smooth" });
      }
    };

    const hero = heroRef.current;
    if (hero) {
      hero.addEventListener("wheel", handleWheel, { passive: true });
    }

    return () => {
      if (hero) {
        hero.removeEventListener("wheel", handleWheel);
      }
    };
  }, []);

  return (
    <div className="font-sans text-gray-800">
      {/* Section Hero plein écran */}
      <section
        ref={heroRef}
        id="hero"
        className="relative h-screen text-white flex items-center justify-center"
        style={{
          backgroundImage: "url('/background.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div className="absolute inset-0 bg-black/40 backdrop-blur-sm"></div>
        <div className="relative z-10 text-center px-4">
          <h1 className="text-6xl md:text-7xl tracking-wide" style={{ fontFamily: "'Raleway', sans-serif", textShadow: "2px 2px 4px rgba(0, 0, 0, 0.5)" }}>Lise & Thibault</h1>
          <p className="text-xl mt-4 mb-6">27 juin 2026</p>
          <Countdown targetDate="2026-06-27T16:00:00" />
          <div className="mt-12 animate-bounce text-white/80">
            <ChevronDown size={32} />
          </div>
        </div>
      </section>

      {/* Contenu principal avec ref pour scroll automatique */}
      <div ref={mainRef} id="main">
        {/* Navigation par onglets */}
        <nav className="sticky top-0 z-10 bg-white shadow flex justify-center space-x-6 py-4">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`px-4 py-2 rounded-full transition-all duration-200 text-sm md:text-base ${
                activeTab === tab.id
                  ? "bg-gray-800 text-white shadow"
                  : "bg-gray-100 text-gray-600 hover:bg-gray-200"
              }`}
            >
              {tab.label}
            </button>
          ))}
        </nav>

        {/* Contenu selon l’onglet actif */}
        <main className="px-4 py-10">
          {activeTab === "programme" && <Programme />}
          {activeTab === "seloger" && <SeLoger />}
          {/* Ajouter d'autres composants pour les autres sections */}
        </main>

        {/* Pied de page */}
        <footer className="text-center text-sm text-gray-500 py-6">
          © 2026 Lise & Thibault
        </footer>
      </div>
    </div>
  );
}

export default App;
