// src/components/Lieu.js
import React from "react";
import Section from "./Section";

const Lieu = () => {
  return (
    <Section>
      <div className="max-w-2xl mx-auto text-gray-800 mt-8">
        <h3 className="text-2xl font-semibold mb-6 text-center">Le lieu</h3>
        <p className="mb-4">
          Le mariage aura lieu au <strong>Château de Séréville</strong>, situé à La Belliole, dans l’Yonne.
        </p>
        <p className="mb-4">
          Un cadre exceptionnel et bucolique, avec de magnifiques jardins et un parc pour profiter pleinement de cette journée unique.
        </p>
        <p className="mb-4">
          Adresse : Château de Séréville, 89150 La Belliole, France
        </p>
        <p>
          <a
            href="https://www.google.com/maps/search/?api=1&query=Ch%C3%A2teau%20de%20S%C3%A9r%C3%A9ville%20-%20Domaine%20de%20S%C3%A9r%C3%A9ville%20-%20wedding%20venue%20Les%20Charlots%2C%2089150%20La%20Belliole%2C%20France" // remplace par le vrai lien Google Maps
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 hover:underline"
          >
            Voir sur Google Maps
          </a>
        </p>
      </div>
    </Section>
  );
};

export default Lieu;
